          if (!client || !bundle || bundle.isEmpty()) {
            return;
          }

          return JSON.stringify({
            type: 'update',
            body: {
              modules: bundle.getModulesIdsAndCode(),
              inverseDependencies: client.inverseDependenciesCache,
