  var WebSocketServer = require('ws').Server;
  var wss = new WebSocketServer({
    server: server,
    path: path });

  var debuggerSocket, clientSocket;

  function send(dest, message) {
    if (!dest) {
      return;
    }

    try {
      dest.send(message);
    } catch (e) {
      console.warn(e);
      // Sometimes this call throws 'not opened'
